exports.setup = function() {
  actor.input('received');
  actor.output('response');
}
var handler = addInputHandler('received', function() {
  var data = get('received');
  var response = {'socketID':data.socketID, 'message': 'Received: ' + data.message};
  send('response', response);
});

exports.wrapup = function() {
  removeInputHandler(handler, 'received');
}
